from elasticsearch import Elasticsearches = Elasticsearch(    hosts=["elasticsearch"],    http_auth=("", ""),    timeout=40,    max_retries=10,    retry_on_timeout=True,)def create_index(name):    es.indices.create(        name,        body={            "mappings": {                "properties": {                    "timestamp": {"type": "date", "format": "epoch_millis"},                    "price": {"type": "keyword"},                }            }        }    )if __name__ == "__main__":    with open('crypto.txt') as f:        infos = f.read().splitlines()    for info in infos:        crypto, _ = info.split(",")        try:            create_index(name=crypto)        except:            print(f"{crypto} index is alredy created")