import timefrom Tracker import CurrencyTrackerif __name__ == "__main__":    with open('crypto.txt') as f:        infos = f.read().splitlines()    processes = []    for info in infos:        crypto, download_time = info.split(",")        processes.append(CurrencyTracker(currency=crypto, download_time=int(download_time)))    for process in processes:        process.start()        time.sleep(1)    print("idle...")    while True:        time.sleep(60)# BTC = CurrencyTracker(currency="bitcoin", download_time=60).start()# DOGE = CurrencyTracker(currency="dogecoin", download_time=60).start()# ETH = CurrencyTracker(currency="ethereum", download_time=60).start()