import matplotlib.pyplot as pltimport pandas as pdclass Visualizer():    def __init__(self, currencies=["bitcoin"]):        self.currencies = currencies        self.data = self.load_data()    def load_data(self):        output = {}        for currency in self.currencies:            try:                data = pd.read_csv(f"{currency}_price.csv", delimiter="\t")[["date", "price"]]                data["date"] = pd.to_datetime(data["date"], infer_datetime_format=True)                data["price"] = data["price"].astype(float)                output[currency] = data            except Exception as genericException:                print(f"{genericException}")        return output    def visualize_multiple(self, currencies=[]):        currencies = self.currencies if currencies == [] else currencies        fig, ax = plt.subplots()        for currency in currencies:            ax.plot(self.data[currency]["date"],                    self.data[currency]["price"],                    label=currency)        ax.set_title(f"price history comparison")        ax.set_xlabel("datetime")        ax.set_ylabel("price")        ax.legend()        return fig    def visualize_single(self, currency):        fig, ax = plt.subplots()        ax.plot(self.data[currency]["date"], self.data[currency]["price"], label="currency")        ax.set_title(f"{currency} price history")        ax.set_xlabel("datetime")        ax.set_ylabel("price")        return figviz = Visualizer(currencies=["bitcoin", "dogecoin", "ethereum"])bitcoin_chart = viz.visualize_single(currency="bitcoin")multiple_chart = viz.visualize_multiple(currencies=["bitcoin", "ethereum"])bitcoin_chart.savefig("fig/bitcoin_chart.png")multiple_chart.savefig("fig/multiple_chart.png")