import datetime as dtimport pandas as pdimport requestsfrom apscheduler.schedulers.background import BackgroundSchedulerfrom bs4 import BeautifulSoup as bsclass CurrencyTracker():    def __init__(self,                 currency="bitcoin",                 download_time=10):        """        Download information about cryptocurrency price from Coinmarketcap.        :param currency: State the currency to be tracked        :param download_time: Rate of price's download (in seconds)        """        self.currency = currency        self.URL = f"https://coinmarketcap.com/currencies/{currency}/"        self.download_time = download_time  # time is in seconds        pd.DataFrame(columns=["date", "price"]).to_csv(f"{self.currency}_price.csv", sep="\t")        self.scheduler = self.init_scheduler()    def init_scheduler(self):        scheduler = BackgroundScheduler()        scheduler.add_job(self.job,                          'interval',                          seconds=self.download_time,                          name=f"{self.currency}_tracker")        return scheduler    def job(self):        r = requests.get(self.URL)        soup = bs(r.content, 'html.parser')        price = soup.find("div", {"class": "priceValue"}).select_one("span").text        price = price.replace(",", "").replace("$", "").strip()        with open(f"{self.currency}_price.csv", 'a') as fd:            fd.write(f"\t{dt.datetime.now()}\t{price}\n")        del r, soup, price    def start(self):        self.scheduler.start()        print(f"{self.currency} tracker started")